#!/bin/bash
# Create placeholder icon files for Story 5.4
# These are minimal valid image files that should be replaced with proper designs

# Create a simple 1x1 transparent PNG (69 bytes - smallest valid PNG)
echo -n 'iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChwGA60e6kgAAAABJRU5ErkJggg==' | base64 -d > placeholder.png

# Copy to all required icon files (will be replaced with proper icons)
cp placeholder.png icon-192.png
cp placeholder.png icon-512.png
cp placeholder.png apple-touch-icon.png

# For favicon.ico, we'll create a minimal valid ICO file
# This is a 16x16 transparent ICO
echo -n '' | base64 -d > favicon.ico

# Cleanup
rm placeholder.png

echo Placeholder
